import React from 'react';
import styled from 'styled-components';
import { Layout } from 'antd';
import { Link } from 'react-router-dom';

import { CurrentUserContext } from '../../contexts/Store';
import { Web3SignIn } from '../account/Web3SignIn';
import { truncateAddr } from '../../utils/Helpers';

const StyledHeaderTellor = styled.span`
  color: white;
  font-size: 36px;
`;
const StyledHeaderData = styled.span`
  color: #53f1b6;
  font-size: 36px;
`;

const HeaderNav = () => {
  const { Header } = Layout;

  return (
    <Header>
      <StyledHeaderTellor>tellor</StyledHeaderTellor>
      <StyledHeaderData>disputes</StyledHeaderData>
      <Link to="/">Home</Link>
      <Link to="/disputes">Disputes</Link>

      {currentUser && currentUser.profile && currentUser.profile.image ? (
        <ReBox>
          <Text color="primary">
            <img
              alt=""
              width="40"
              height="40"
              style={{ backgroundColor: '#b5b5b5' }}
              src={
                currentUser.profile.image
                  ? `https://ipfs.infura.io/ipfs/${currentUser.profile.image[0].contentUrl['/']}`
                  : null
              }
            />
            {truncateAddr(currentUser.username)}
          </Text>
        </ReBox>
      ) : (
        <>
          {currentUser && currentUser.username ? (
            <ReBox>
              <Text color="primary">
                <img
                  alt=""
                  width="40"
                  height="40"
                  style={{ backgroundColor: '#b5b5b5' }}
                  src={makeBlockie(currentUser.username)}
                />
                {truncateAddr(currentUser.username)}
              </Text>
            </ReBox>
          ) : null}
        </>
      )}
    </Header>
  );
};

export default HeaderNav;
